/**
 * Simple links list block
 *
 * @category   Mage
 * @package    Mage_Core
 * @author      Magento Core Team <core@magentocommerce.com>
 */
class Monbento_Page_Block_Template_Navigation extends Mage_Page_Block_Template_Links
{

    /**
     * Set default template
     *
     */
    protected function _construct()
    {
        $this->setTemplate('page/template/navigation.phtml');
    }
    
    public function addLink($label, $url='', $title='', $prepare=false, $urlParams=array(),
        $position=null, $liParams=null, $aParams=null, $beforeText='', $afterText='')
    {
        $url = str_replace('{{site_url}}', $this->getUrl(), $url);
        
        return parent::addLink($label, $url, $title, $prepare, $urlParams, $position, $liParams, $aParams, $beforeText, $afterText);
    }
}
