<?php/** * The Magento Developer * http://themagentodeveloper.com * * @category   Sellry * @package    Sellry_OrderSync * @version    0.1.3 */ $installer = $this;$installer->startSetup();$this->run(    "DROP TABLE IF EXISTS {$this->getTable('ordersync/inventory')};    CREATE TABLE `{$this->getTable('ordersync/inventory')}`        (            `id` int(11) unsigned NOT NULL AUTO_INCREMENT,            `product_id` int(11) unsigned NOT NULL,            `allow_export` tinyint(1) NOT NULL DEFAULT '0',            `allow_stock_update` tinyint(1) NOT NULL DEFAULT '0',            `allow_export_default` tinyint(1) NOT NULL DEFAULT '1',            `allow_stock_update_default` tinyint(1) NOT NULL DEFAULT '1',            PRIMARY KEY (`id`)        ) ENGINE=InnoDB DEFAULT CHARSET=utf8;");    $this->run("    ALTER TABLE `{$this->getTable('sales/order')}` ADD `sync_inventory_status` VARCHAR (4096) default NULL;");$this->endSetup();