r‘:^<?php exit; ?>a:1:{s:7:"content";a:47:{s:8:"produits";a:1:{i:0;s:1:"2";}s:18:"penci_recipe_title";a:1:{i:0;s:17:"Carrot tops pesto";}s:21:"penci_recipe_servings";a:1:{i:0;s:0:"";}s:21:"penci_recipe_preptime";a:1:{i:0;s:10:"20 minutes";}s:28:"penci_recipe_preptime_format";a:1:{i:0;s:0:"";}s:21:"penci_recipe_cooktime";a:1:{i:0;s:10:"10 minutes";}s:28:"penci_recipe_cooktime_format";a:1:{i:0;s:0:"";}s:25:"penci_recipe_instructions";a:1:{i:0;s:1060:"&lt;p&gt;&lt;em&gt;For the first container:&lt;/em&gt;&lt;br /&gt;Clean the carrots and their tops.&lt;br /&gt;Blend the carrot tops, almonds, cashew nuts, parmesan, the clove of garlic and olive oil to obtain a pesto. Add salt and pepper.&lt;br /&gt;Cook the farfalle according to the instructions on the package.&lt;br /&gt;Mix the cooked pasta with the pesto and put the mixture into the MB Original bento box.&lt;br /&gt;&lt;br /&gt;Keep the remaining pesto for another recipe (to eat within a few days).&lt;/p&gt;
&lt;p&gt;&lt;em&gt;For the second container:&lt;/em&gt;&lt;br /&gt;Slice the carrots into sticks.&lt;br /&gt;Slice the grapefruit in halves and make thin slices.&lt;br /&gt;Put the carrots and grapefruit pieces in the second container of the bento box.&lt;br /&gt;&lt;br /&gt;For the food cup, mix the soy yogurt, lemon juice, and chopped chives.&lt;br /&gt;&lt;br /&gt;&lt;strong&gt;The Zero Waste Tip:&lt;/strong&gt; &lt;br /&gt;Buy in bulk, bring your own containers&lt;br /&gt;Use organic fruit and veggies to keep the skin on&lt;/p&gt;";}s:24:"penci_recipe_ingredients";a:1:{i:0;s:356:"<p>For 1 MB Original bento box:<br />- 100g of Farfalles<br />- A bunch of carrots with tops<br />- One clove of garlic<br />- A grapefruit<br />- A soya yogurt<br />- 25g of almonds<br />- 25g of cashew nuts (unsalted)<br />- Few blades of chives<br />- 80g of olive oil<br />- 2 Tbsp of parmesan<br />- 1 Tbsp of lemon juice<br />- Salt<br />- Pepper</p>";}s:17:"penci_recipe_note";a:1:{i:0;s:0:"";}s:21:"penci_recipe_calories";a:1:{i:0;s:0:"";}s:16:"penci_recipe_fat";a:1:{i:0;s:0:"";}s:21:"penci_recipe_keywords";a:1:{i:0;s:0:"";}s:20:"penci_recipe_cuisine";a:1:{i:0;s:0:"";}s:20:"penci_recipe_videoid";a:1:{i:0;s:0:"";}s:23:"penci_recipe_videotitle";a:1:{i:0;s:0:"";}s:26:"penci_recipe_videoduration";a:1:{i:0;s:0:"";}s:22:"penci_recipe_videodate";a:1:{i:0;s:0:"";}s:21:"penci_recipe_videodes";a:1:{i:0;s:0:"";}s:33:"penci_custom_sidebar_page_display";a:1:{i:0;s:0:"";}s:26:"penci_post_sidebar_display";a:1:{i:0;s:0:"";}s:18:"penci_single_style";a:1:{i:0;s:0:"";}s:27:"penci_pfeatured_image_ratio";a:1:{i:0;s:0:"";}s:28:"penci_enable_jarallax_single";a:1:{i:0;s:0:"";}s:24:"penci_post_hide_featuimg";a:1:{i:0;s:0:"";}s:22:"penci_post_views_count";a:1:{i:0;s:3:"172";}s:27:"penci_post_week_views_count";a:1:{i:0;s:1:"2";}s:28:"penci_post_month_views_count";a:1:{i:0;s:3:"172";}s:23:"penci_recipe_rate_total";a:1:{i:0;s:1:"5";}s:24:"penci_recipe_rate_people";a:1:{i:0;s:1:"1";}s:13:"_thumbnail_id";a:1:{i:0