r‘:^<?php exit; ?>a:1:{s:7:"content";a:48:{s:8:"produits";a:1:{i:0;s:1:"3";}s:18:"penci_recipe_title";a:1:{i:0;s:45:"Toasts from the woods and small sunny muffins";}s:21:"penci_recipe_servings";a:1:{i:0;s:0:"";}s:21:"penci_recipe_preptime";a:1:{i:0;s:10:"15 minutes";}s:28:"penci_recipe_preptime_format";a:1:{i:0;s:0:"";}s:21:"penci_recipe_cooktime";a:1:{i:0;s:10:"30 minutes";}s:28:"penci_recipe_cooktime_format";a:1:{i:0;s:0:"";}s:25:"penci_recipe_instructions";a:1:{i:0;s:1176:"&lt;p&gt;&lt;em&gt;For the first level:&lt;/em&gt; &lt;br /&gt;Cover the bottom of the bento with some shoots of lambâ€™s lettuce.&lt;br /&gt;Wash and cut the carrot and each in sticks. (Set aside the foliage that will be used for the second container).&lt;br /&gt;Rince and cut the mushrooms in slices. &lt;br /&gt;Place the slices on the toasted bread. &lt;br /&gt;Sprinkle the toasts with the herbs of Provence and a drizzle of olive oil. &lt;br /&gt;Brown in the oven for 10 minutes, then place the toasts in your bento.&lt;/p&gt;
&lt;p&gt;&lt;em&gt;For the muffins:&lt;/em&gt; &lt;br /&gt;In a salad bowl, mix the flour, olive oil, almond milk, pesto, dried tomatoes and the chopped carrot foliage. &lt;br /&gt;Pour the mixture in the muffin tins. &lt;br /&gt;Bake the muffins for 15 to 20 minutes at 180Â°C.&lt;/p&gt;
&lt;p&gt;This recipe goes along well with almonds and pear.&lt;/p&gt;
&lt;p&gt;&lt;strong&gt;Little tips for zero-waste:&lt;/strong&gt;&lt;br /&gt;- Buy vegetables in bulk&lt;br /&gt;- Prefer ORGANIC vegetables&lt;br /&gt;- Use the previous day's bread for the toasts. &lt;br /&gt;- Use the whole of the vegetable (peels &amp;amp; foliage)&lt;/p&gt;";}s:24:"penci_recipe_ingredients";a:1:{i:0;s:384:"<p>Ingredients for 2 MB Element:</p>
<p><em>For the toasts:</em><br />- 3 button mushrooms <br />- A few shoots of lamb's lettuce<br />- 2 slices of toasted bread<br />- A pinch of herbs of Provence</p>
<p><em>For 4 muffins:</em><br />- 60g of flour <br />- 1 Tbsp of oil <br />- 60g almond milk<br />- 1 Tbsp of pesto <br />- Some dried tomatoes<br />- 1 carrot and its foliage</p>";}s:17:"penci_recipe_note";a:1:{i:0;s:0:"";}s:21:"penci_recipe_calories";a:1:{i:0;s:0:"";}s:16:"penci_recipe_fat";a:1:{i:0;s:0:"";}s:21:"penci_recipe_keywords";a:1:{i:0;s:0:"";}s:20:"penci_recipe_cuisine";a:1:{i:0;s:0:"";}s:20:"penci_recipe_videoid";a:1:{i:0;s:0:"";}s:23:"penci_recipe_videotitle";a:1:{i:0;s:0:"";}s:26:"penci_recipe_videoduration";a:1:{i:0;s:0:"";}s:22:"penci_recipe_videodate";a:1:{i:0;s:0:"";}s:21:"penci_recipe_videodes";a:1:{i:0;s:0:"";}s:33:"penci_custom_sidebar_page_display";a:1:{i:0;s:0:"";}s:26:"penci_post_sidebar_display";a:1:{i:0;s:0:"";}s:18:"penci_single_style";a:1:{i:0;s:0:"";}s:27:"penci_pfeatured_image_ratio";a:1:{i:0;s:0:"";}s:28:"penci_enable_jarallax_single";a:1:{i:0;s:0:"";}s:24:"penci_post_hide_featuimg";a:1:{i:0;s:0:"";}s:22:"penci_post_views_count";a:1:{i:0;s:2:"87";}s:27:"penci_post_week_views_count";a:1:{i:0;s:1:"2";}s:28:"penci_post_month_views_count";a:1:{i:0;s:2:"87";}s:23:"penci_recipe_rate_total";a:1:{i:0;s:1:"5";}s:24:"penci_recipe_rate_people";a:1:{i:0;s:1:"1";}s:13:"_thumbnail_id";a:1:{i:0;s:5:"12972";}s:29:"_yoast_wpseo_primary_category";a:1:{i:0;s:3:"479";}s:10:"_edit_lock";a:1:{i:0;s:12:"1580397510:9";}s:16:"_format_link_url";a:1:{i:0;s:0:"";}s:24:"_format_quote_source_url";a:1:{i:0;s:0:"";}s:25:"_format_quote_source_name";a:1:{i:0;s:0:"";}s:19:"_format_audio_embed";a:1:{i:0;s:0:"";}s:19:"_format_video_embed";a:1:{i:0;s:0:"";}s:10:"_edit_last";a:1:{i:0;s:1:"9";}s:9:"_produits";a:1:{i:0;s:19:"field_5d8b6efdde49c";}s:22:"_format_gallery_images";a:1:{i:0;s:22:"a:1:{i:0;s:5:"12975";}";}s:26:"_yoast_wpseo_content_score";a:1:{i:0;s:2:"30";}s:18:"produits_0_produit";a:1:{i:0;s:5:"12561";}s:19:"_produits_0_produit";a:1:{i:0;s:19:"field_5d8b6f09de49d";}s:18:"produits_1_produit";a:1:{i:0;s:5:"12593";}s:19:"_produits_1_produit";a:1:{i:0;s:19:"field_5d8